let Bulb3W = new Product("Bulb 3W", 3, 25000, 2, "https://cf.shopee.vn/file/58bf39d876e15f965f56901c7c39eff7", 50000 );
let Bulb5W = new Product("Bulb 5W", 5, 30000, 2, "data:image/jpeg;base64,", 60000);

let product = [Bulb3W,Bulb5W];
let indexEdit = -1;

function show() {
    let str = '';
    for (let i = 0; i < product.length; i++) {
        let linkHref = "";
        if (product[i].capacity === 3) {
            linkHref = "http://localhost:63342/Javascript/Case/3W.html?_ijt=7o7tqljvs4dkme7ok3babthtdo&_ij_reload=RELOAD_ON_SAVE";
        } else if (product[i].capacity === 5) {
            linkHref = "http://localhost:63342/Javascript/Case/5W.html?_ijt=6m9mbd4ns7eitk38kmajatqkpq&_ij_reload=RELOAD_ON_SAVE";
        }
        console.log(linkHref)
        str += `<tr>
              <td>${product[i].name}</td>
              <td>${product[i].capacity}</td>
              <td>${product[i].price}</td>
              <td>${product[i].quantity}</td>
              <td><img src="${product[i].img}" width="200" height="200"></td>
              <td>${product[i].total}</td>
              <td><button onclick="clickE('${linkHref}')">Click</button></td>
              <td><button onclick="showEdit(${i})">Edit</button></td>
              <td><button onclick="deleteNumber(${i})">Delete</button></td>
           </tr>`;
    }
    let element = document.getElementById("display");
    element.innerHTML = str;
}
    function add() {
        let name = document.getElementById("name").value;
        let capacity = document.getElementById("capacity").value;
        let price = document.getElementById("price").value;
        let quantity = document.getElementById("quantity").value;
        let img = document.getElementById("img").value;

        let total = quantity * price;
        let detail = "placeHolder";

        console.log(total);

        let newObj = new Product(name, capacity, price, quantity, img, total, detail);
        if (indexEdit === -1) {
            if (!checkName()) {
                alert("Trung san pham roi");
                return;
            }
            product.push(newObj);
        } else {
            product[indexEdit] = newObj;
            indexEdit = -1;
        }
        show();
    }

    function deleteNumber(index) {
        product.splice(index, 1);
        show();
    }

    function showEdit(index) {
        indexEdit = index;
        document.getElementById("name").value = product[index].name;
        document.getElementById("capacity").value = product[index].capacity;
        document.getElementById("price").value = product[index].price;
        document.getElementById("quantity").value = product[index].quantity;
        document.getElementById("img").value = product[index].img;

    }

function clickE(link) {
    window.location.href = link;
}

    function checkName() {
        let name = document.getElementById("name").value;
        for (let i = 0; i < product.length; i++) {
            if (product[i].name === name) {
                return false;
            }
        }
        return true;

    }

    show();
